mapper.select:
SELECT zi.* FROM zs_info zi



        AND zi.`name` like concat('%',#{name},'%')


        AND zi.code = #{code}


        AND zi.dept_name = #{deptName}


        AND zi.area_id = #{areaId}



        AND zi.category_type = #{categoryType}


        AND zi.category_id = #{categoryId}


    AND zi.status =1
    and zi.audit_status =1

    order by zi.create_time desc

SELECT zi.*,zs.name categoryName FROM zs_personal zi
left  join zs_category zs on zi.category_id =zs.id



        AND zi.`name`  like concat('%',#{name},'%')


        AND zi.code = #{code}


        AND zi.dept_name = #{deptName}


        AND zi.audit_status = #{auditStatus}


        AND zi.short_code like concat('',#{shortCode},'%')


order by zi.create_time desc

SELECT zi.* FROM zs_info zi



        AND zi.`name`  = #{name}


        AND zi.code = #{code}


        AND zi.dept_name = #{deptName}

    AND zi.status =1
    and zi.audit_status =1

order by zi.create_time desc

select aa.a east,bb.b northWest,cc.c south,dd.d north from (
(select count(*) a from zs_info
AND area_id = #{areaId}
and area_id = 26 and status =1 and audit_status =1
) aa , ( select count(*) b from zs_info
AND area_id = #{areaId}
and area_id = 27 and status =1 and audit_status =1
) bb , ( select count(*) c from zs_info
AND area_id = #{areaId}
and area_id = 28 and status =1 and audit_status =1
) cc , (select count(*) d from zs_info
AND area_id = #{areaId}
and area_id = 29 and status =1 and audit_status =1
) dd )

select  aa.a addInfo,bb.b reduceInfo from ((select count(*) a from zs_info where  YEAR ( create_time ) = #{year}) aa ,

(select count(*) b from zs_info where  YEAR ( sx_time ) = #{year}
and sx_time  <  now() ) bb
)
