mapper.select:
SELECT i.*,s.`name` sportName, (i.height_score+i.ibm_score+i.balance_score+i.coordinate_score+i.flexibility_score+i.sensitive_score+i.legs_score+i.limb_score) total FROM item i LEFT JOIN sport s on i.sport_id = s.id
AND i.`name` like concat('%',#{childName},'%')
AND i.parent_name like concat('%',#{parentName},'%')
AND i.create_by like concat('%',#{createBy},'%')
AND i.phone = #{phone}
AND s.`name` = #{name}
AND s.area_id = #{areaId}
and i.state = 1
ORDER BY i.create_time DESC

SELECT i.*,s.`name` sportName,
(i.height_score+i.ibm_score+i.balance_score+i.coordinate_score+i.flexibility_score+i.sensitive_score+i.legs_score+i.limb_score) total
 FROM item i
LEFT JOIN sport s on i.sport_id = s.id


        AND i.`name` like concat('%',#{childName},'%')


        AND i.create_by  like concat('%',#{createBy},'%')


        AND i.phone = #{phone}


        AND s.`name`   = #{name}


        AND  s.area_id = #{areaId}

    and i.state = 1

ORDER BY ${orderBy}

SELECT i.*,s.`name` sportName,
(i.height_score+i.ibm_score+i.balance_score+i.coordinate_score+i.flexibility_score+i.sensitive_score+i.legs_score+i.limb_score) total
FROM item i
LEFT JOIN sport s on i.sport_id = s.id


        AND i.`name` like concat('%',#{childName},'%')


        AND i.create_by  like concat('%',#{createBy},'%')


        AND i.phone = #{phone}


        AND s.`name`   = #{name}


        AND  s.area_id = #{areaId}

    and i.state = 1

ORDER BY ${orderBy}

SELECT i.*,s.`name` sportName, (i.height_score+i.ibm_score+i.balance_score+i.coordinate_score+i.flexibility_score+i.sensitive_score+i.legs_score+i.limb_score) total FROM item i LEFT JOIN sport s on i.sport_id = s.id
AND i.`name` like concat('%',#{childName},'%')
AND i.phone = #{phone}
AND i.sport_id = #{sportId}
and i.state = 1
ORDER BY i.create_time desc
