mapper.select:
SELECT i.*,s.`name` sportName, ( i.height_score + i.ibm_score + i.fei_hl_score + i.tiaos_score + i.sensitive_score + i.flexibility_score ) total FROM xx_item i LEFT JOIN sport s on i.sport_id = s.id
AND i.`name` like concat('%',#{childName},'%')
AND i.parent_name like concat('%',#{parentName},'%')
AND i.create_by like concat('%',#{createBy},'%')
AND i.phone = #{phone}
AND s.`name` = #{name}
AND s.area_id = #{areaId}
and i.state = 1
ORDER BY i.create_time DESC

SELECT i.*,s.`name` sportName,
( i.height_score + i.ibm_score + i.fei_hl_score + i.tiaos_score + i.sensitive_score + i.flexibility_score ) total
FROM xx_item i
LEFT JOIN sport s on i.sport_id = s.id


        AND i.`name` like concat('%',#{childName},'%')


        AND i.create_by  like concat('%',#{createBy},'%')


        AND i.phone = #{phone}


        AND s.`name`   = #{name}


        AND  s.area_id = #{areaId}

    and i.state = 1

ORDER BY ${orderBy}

SELECT i.*,s.`name` sportName,
( i.height_score + i.ibm_score + i.fei_hl_score + i.tiaos_score + i.sensitive_score + i.flexibility_score ) total
FROM xx_item i
LEFT JOIN sport s on i.sport_id = s.id


        AND i.`name` like concat('%',#{childName},'%')


        AND i.create_by  like concat('%',#{createBy},'%')


        AND i.phone = #{phone}


        AND s.`name`   = #{name}


        AND  s.area_id = #{areaId}

    and i.state = 1

ORDER BY ${orderBy}

SELECT i.*,s.`name` sportName, ( i.height_score + i.ibm_score + i.fei_hl_score + i.tiaos_score + i.sensitive_score + i.flexibility_score ) total FROM xx_item i LEFT JOIN sport s on i.sport_id = s.id
AND i.`name` like concat('%',#{childName},'%')
AND i.phone = #{phone}
AND i.sport_id = #{sportId}
and i.state = 1
ORDER BY i.create_time desc

SELECT
a.nan nanHg,
b.nv nvHg,
c.nanTotal nanTotal,
d.nvTotal nvTotal
FROM
(
(
SELECT
count( 1 ) AS nan
FROM
xx_item i

    and (
    i.height + i.ibm + i.fei_hl + i.tiaos + i.flexibility + i.sensitives
    ) >= 60
    AND i.state = 1
    AND i.sex = 1

        AND i.age   >=   #{start}


        AND i.age   <=   #{end}


) a,
(
SELECT
count( 1 ) AS nv
FROM
xx_item i


    and (
    i.height + i.ibm + i.fei_hl + i.tiaos + i.flexibility + i.sensitives
    ) >= 60
    AND i.state = 1
    AND i.sex = 2

        AND i.age   >=   #{start}


        AND i.age   <=   #{end}


) b,
( SELECT count( 1 ) AS nanTotal FROM xx_item i  and i.state = 1 AND i.sex = 1

    AND i.age   >=   #{start}


    AND i.age   <=   #{end}


) c,
( SELECT count( 1 ) AS nvTotal FROM xx_item i  and i.state = 1 AND i.sex = 2

    AND i.age   >=   #{start}


    AND i.age   <=   #{end}


) d
)

SELECT nan.nan nanTotal,nv.nv nvTotal,nanHg.`nan` nanHeightHg,nvHg.nv nvHeightHg, nanFhl.`nan` nanFeiHlHg, nvFhl.nv nvFeiHlHg,nanTs.`nan` nanTiaosHg, nvTs.nv nvTiaosHg, nanFle.`nan` nanFlexibilityHg , nvFle.nv nvFlexibilityHg,nanSen.`nan` nanSensitivesHg, nvSen.nv nvSensitivesHg, nanIbm.`nan` nanIbmHg ,nvIbm.nv nvIbmHg
FROM (
(SELECT count( 1 ) as nan FROM xx_item i LEFT JOIN xx_avg a ON i.age = a.age
AND i.sex = 1 AND a.sex = 1
AND i.age >= #{start}
AND i.age <= #{end}
) nan,
(SELECT count( 1 ) AS nv FROM xx_item i LEFT JOIN xx_avg a ON i.age = a.age
AND i.sex = 2 AND a.sex = 2
AND i.age >= #{start}
AND i.age <= #{end}
) nv,
(SELECT count( 1 ) as nan FROM xx_item i LEFT JOIN xx_avg a ON i.age = a.age
AND i.sex = 1 AND a.sex = 1 AND i.height > a.height_avg
AND i.age >= #{start}
AND i.age <= #{end}
) nanHg, (SELECT count( 1 ) AS nv FROM xx_item i LEFT JOIN xx_avg a ON i.age = a.age
AND i.sex = 2 AND a.sex = 2 AND i.height > a.height_avg
AND i.age >= #{start}
AND i.age <= #{end}
) nvHg,
(SELECT count( 1 ) as nan FROM xx_item i LEFT JOIN xx_avg a ON i.age = a.age
and i.sex = 1 AND a.sex = 1 AND i.fei_hl > a.fei_hl_avg
AND i.age >= #{start}
AND i.age <= #{end}
) nanFhl,
(SELECT count( 1 ) AS nv FROM xx_item i LEFT JOIN xx_avg a ON i.age = a.age
and i.sex = 2 AND a.sex = 2 AND i.fei_hl > a.fei_hl_avg
AND i.age >= #{start}
AND i.age <= #{end}
) nvFhl,
(SELECT count( 1 ) as nan FROM xx_item i LEFT JOIN xx_avg a ON i.age = a.age
and i.sex = 1 AND a.sex = 1 AND i.tiaos > a.tiaos_avg
AND i.age >= #{start}
AND i.age <= #{end}
) nanTs,
(SELECT count( 1 ) AS nv FROM xx_item i LEFT JOIN xx_avg a ON i.age = a.age
and i.sex = 2 AND a.sex = 2 AND i.tiaos > a.tiaos_avg
AND i.age >= #{start}
AND i.age <= #{end}
) nvTs,
(SELECT count( 1 ) as nan FROM xx_item i LEFT JOIN xx_avg a ON i.age = a.age
and i.sex = 1 AND a.sex = 1 AND i.flexibility > a.flexibility_avg
AND i.age >= #{start}
AND i.age <= #{end}
) nanFle,
(SELECT count( 1 ) AS nv FROM xx_item i LEFT JOIN xx_avg a ON i.age = a.age
and i.sex = 2 AND a.sex = 2 AND i.flexibility > a.flexibility_avg
AND i.age >= #{start}
AND i.age <= #{end}
) nvFle,
(SELECT count( 1 ) as nan FROM xx_item i LEFT JOIN xx_avg a ON i.age = a.age
and i.sex = 1 AND a.sex = 1 AND i.sensitives > a.sensitive_avg
AND i.age >= #{start}
AND i.age <= #{end}
) nanSen,
(SELECT count( 1 ) AS nv FROM xx_item i LEFT JOIN xx_avg a ON i.age = a.age
and i.sex = 2 AND a.sex = 2 AND i.sensitives > a.sensitive_avg
AND i.age >= #{start}
AND i.age <= #{end}
) nvSen,
(SELECT count( 1 ) as nan FROM xx_item i LEFT JOIN xx_avg a ON i.age = a.age
and i.sex = 1 AND a.sex = 1 AND i.ibm > a.ibm_avg
AND i.age >= #{start}
AND i.age <= #{end}
) nanIbm,
(SELECT count( 1 ) AS nv FROM xx_item i LEFT JOIN xx_avg a ON i.age = a.age
and i.sex = 2 AND a.sex = 2 AND i.ibm > a.ibm_avg
AND i.age >= #{start}
AND i.age <= #{end}
) nvIbm
)
