SELECT i.*,s.`name` sportName FROM xx_item i LEFT JOIN sport s on i.sport_id = s.id
AND i.`name` like concat('%',#{childName},'%')
AND i.phone = #{phone}
AND i.sport_id = #{sportId}
and i.state = 1
ORDER BY i.create_time desc
